            return new Promise((res, rej) => {
                QRCode.toCanvas(canvas, data, {
                    margin: 2,
                    scale: 2
                }, (err, tg) => !err ? res(tg) : rej(err));
            });
        }
        
        writeQr(canvas, inputs.text);

    },

